// src/components/EventCard.js
import React from 'react';
import './EventCard.css';

function EventCard({ event }) {
  // Adjust to match the actual event object fields in the Probo API
  const {
    id,
    title,    // Example: "New Zealand to win the 2nd ODI vs Sri Lanka?"
    subTitle, // Example: "H2H last 5 ODI: NZ 2, SL 2, DRAW 1"
    stats,    // Example: "19253" (or similar)
    options,  // Example: array of { label: "Yes", price: "₹7.5" }, etc.
  } = event;

  return (
    <div className="event-card">
      <h3>{title}</h3>
      {subTitle && <p className="subtitle">{subTitle}</p>}
      {stats && <div className="stats">{stats} traders</div>}

      {Array.isArray(options) && (
        <div className="options">
          {options.map((opt, idx) => (
            <button key={idx} className="option-button">
              {opt.label} {opt.price}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

export default EventCard;
