// src/App.js
import React, { useEffect, useState } from 'react';
import './App.css';

import { fetchProboEvents } from './api';
import EventCard from './components/EventCard';

function App() {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function getData() {
      setLoading(true);
      const data = await fetchProboEvents();

      // Adjust according to the actual data shape:
      // If data.data.items is an array, we store it in 'events'
      if (data && data.data && data.data.items) {
        setEvents(data.data.items);
      }
      setLoading(false);
    }

    getData();
  }, []);

  return (
    <div className="App">
      <h1>Probo Events</h1>
      {loading && <p>Loading events...</p>}

      <div className="events-container">
        {!loading && events.map((eventItem) => (
          <EventCard key={eventItem.id} event={eventItem} />
        ))}
      </div>
    </div>
  );
}

export default App;
