// src/components/EventCard.js
import React from 'react';
import './EventCard.css';

function EventCard({ event }) {
  // The actual fields may differ in the real API:
  // Checking the structure, we might see something like:
  // event = {
  //   id: 123,
  //   title: "New Zealand to win the 2nd ODI vs Sri Lanka?",
  //   subTitle: "H2H last 5 ODI: ...",
  //   stats: 27763,
  //   options: [
  //     { label: "Yes", price: "₹9.5" },
  //     { label: "No", price: "₹0.5" }
  //   ]
  // }

  const {
    id,
    title,       // e.g. "New Zealand to win the 2nd ODI vs Sri Lanka?"
    subTitle,    // e.g. "H2H last 5 ODI: NZ 2, SL 2, DRAW 1 Read more"
    stats,       // e.g. 27763
    options      // e.g. [{label: 'Yes', price: '₹9.5'}, {label: 'No', price: '₹0.5'}]
  } = event;

  // The "stats" might be just a number, so we display it as "#### traders"
  const tradersText = stats ? `${stats} traders` : '';

  return (
    <div className="event-card">
      {tradersText && <div className="traders-count">{tradersText}</div>}

      {/* Title */}
      <h3 className="event-title">{title}</h3>

      {/* Subtitle */}
      {subTitle && <p className="event-subtitle">{subTitle}</p>}

      {/* Buttons row */}
      {Array.isArray(options) && (
        <div className="event-options">
          {options.map((opt, idx) => (
            <button
              key={idx}
              className={`option-button ${
                opt.label.toLowerCase() === 'yes' ? 'yes-btn' : 'no-btn'
              }`}
            >
              {opt.label} {opt.price}
            </button>
          ))}
        </div>
      )}
    </div>
  );
}

export default EventCard;
