import React, { useEffect, useState } from 'react';
import EventCard from './components/EventCard';
import './App.css';

const App = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch('https://prod.api.probo.in/api/v2/feed/public/homefeed', {
          headers: {
            'Content-Type': 'application/json',
            'x-device-os': 'DESKTOP',
            'x-version-name': '10',
            'appid': 'in.probo.pro',
          },
        });
        const data = await response.json();
        setEvents(data.data.records || []); // Accessing the 'records' key
      } catch (error) {
        console.error('Error fetching events:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchEvents();
  }, []);

  return (
    <div className="app">
      {/* Add the navbar image */}
      <img
        src="/Users/dhruvsood/Desktop/Probo_Assignments/ReactJS_assign/my-app/public/nav_bar.png"
        alt="Navbar"
        style={{ width: '100%', height: 'auto' }}
      />
      <h1>Probo Events</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="events-grid">
          {events.map((event) => (
            <EventCard key={event.id} event={event} />
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
