import React, { useEffect, useState } from 'react';
import './EventCard.css';

const EventCard = ({ event }) => {
  const [remainingTime, setRemainingTime] = useState(null);

  useEffect(() => {
    if (event.expiry_date_time_stamp) {
      const interval = setInterval(() => {
        const now = new Date();
        const expiryDate = new Date(event.expiry_date_time_stamp);
        const diff = expiryDate - now;

        if (diff <= 0) {
          setRemainingTime('Time is up!');
          clearInterval(interval);
        } else {
          const hours = Math.floor(diff / (1000 * 60 * 60));
          const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
          const seconds = Math.floor((diff % (1000 * 60)) / 1000);
          setRemainingTime(`${hours}h ${minutes}m ${seconds}s`);
        }
      }, 1000);

      return () => clearInterval(interval); // Cleanup the interval
    }
  }, [event.expiry_date_time_stamp]);

  const [animation, setAnimation] = useState(null);

  const handleYesClick = () => {
    setAnimation('blue'); // Trigger blue fill animation for "Yes"
    setTimeout(() => {
      setAnimation(null); // Reset animation after it's done
    }, 1000); // Match the duration of the animation
  };

  const handleNoClick = () => {
    setAnimation('red'); // Trigger red fill animation for "No"
    setTimeout(() => {
      setAnimation(null); // Reset animation after it's done
    }, 1000); // Match the duration of the animation
  };

  return (
    <div className="event-card-container">
      <div
        className={`event-card ${animation === 'blue' ? 'blue-fill' : ''} ${
          animation === 'red' ? 'red-fill' : ''
        }`}
      >
        <img src={event.image_url} alt={event.name} className="event-image" />
        <h3>{event.name}</h3>
        <p>{event.oneliner?.text}</p>
        <div className="traders">
          <span>{event.traders_count_numeric} traders</span>
        </div>
        <div className="actions">
          <button
            className="yes animated-button"
            style={{
              backgroundColor: event.buy_button.bg_color,
              color: event.buy_button.text_color,
            }}
            onClick={handleYesClick} // Trigger blue animation on Yes click
          >
            {event.buy_button.text}
          </button>
          <button
            className="no animated-button"
            style={{
              backgroundColor: event.sell_button.bg_color,
              color: event.sell_button.text_color,
            }}
            onClick={handleNoClick} // Trigger red animation on No click
          >
            {event.sell_button.text}
          </button>
        </div>
        <div className="expiry">
          {remainingTime ? <p className="timer">Time left: {remainingTime}</p> : null}
        </div>
      </div>
    </div>
  );
};

export default EventCard;
